&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;apt install php libapache2-mod-php php-mbstring php-cli php-bcmath php-json php-xml php-zip php-pdo php-common php-tokenizer php-mysql
&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;php --version&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;curl -sS https://getcomposer.org/installer | php&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;if Curl not isntalled&lt;/p&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;apt install curl&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;mv composer.phar /usr/local/bin/composer&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;chmod +x /usr/local/bin/composer&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;composer --version&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;apt-get install php phpmyadmin&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;if there is connection error (Mainly in docker) In File &lt;code&gt;/etc/phpmyadmin/config-db.php&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;change &lt;code&gt;$dbserver=&#039;localhost&#039;;&lt;/code&gt; to &lt;code&gt;$dbserver=&#039;127.0.0.1&#039;;&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;restart apache &lt;code&gt;service apache2 restart&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;check if phpmyadmin user is exist&lt;br&gt;&lt;br&gt;&lt;/p&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;SELECT * from mysql.user&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;CREATE USER &#039;phpmyadmin &#039;@&#039;localhost&#039; IDENTIFIED BY &#039;password&#039;;&lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;cd /etc/apache2/sites-available&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;sudo cp 000-default.conf laravel.conf&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;sudo nano laravel.conf
&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;&lt;VirtualHost *:80&gt;
    ServerAdmin admin@example.com
    ServerName laravel.example.com
    DocumentRoot /var/www/html/laravel-realworld-example-app/public/

    &lt;Directory /var/www/html/laravel-realworld-example-app/public/&gt;
            AllowOverride All
            Require all granted
    &lt;/Directory&gt;

    ErrorLog ${APACHE_LOG_DIR}/error.log
    CustomLog ${APACHE_LOG_DIR}/access.log combined
&lt;/VirtualHost&gt;&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;a2dissite 000-default.conf&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;a2enmod rewrite
a2ensite laravel.conf
service apache2 restart &lt;/code&gt;&lt;/pre&gt;

&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;composer require laravel/breeze --dev&lt;/code&gt;&lt;/pre&gt;
&lt;pre class=&quot;language-markup&quot;&gt;&lt;code&gt;php artisan breeze:install

php artisan migrate
npm install
npm run dev&lt;/code&gt;&lt;/pre&gt;

